mapper.update:
update tb_eventcomment set
                     evc_commenter = #{evc_commenter},
                     evc_content = #{evc_content},
                     evc_update = #{evc_update}
where evc_no = #{evc_no}
  and ev_no = #{ev_no}

mapper.delete:
delete from tb_eventcomment
where evc_no = #{evc_no}
  and ev_no = ${ev_no}
