mapper.insert:
INSERT INTO TB_MEMBER (
                       MEM_ID
                       , MEM_PW
                       , MEM_NAME
                       , MEM_PHONE
                       , MEM_ZIPCODE
                       , MEM_ADDRESS
                       , MEM_ADDRESS2
                       , MEM_GENDER
                       , MEM_CDATE
                       , MEM_BIRTHDAY
                       , MEM_EMAIL
                       , MEM_NICKNAME
                       )
VALUES (
        #{mem_id}
        ,#{mem_pw}
        ,#{mem_name}
        ,#{mem_phone}
        ,#{mem_zipcode}
        ,#{mem_address}
        ,#{mem_address2}
        ,#{mem_gender}
        , sysdate
        ,#{mem_birthday}
        ,#{mem_email}
        ,#{mem_nickname}
        )

mapper.update:
UPDATE tb_member
SET  mem_zipcode = #{mem_zipcode}
    ,mem_address = #{mem_address}
    ,mem_address2 = #{mem_address2}
    ,mem_email = #{mem_email}
    ,mem_update = sysdate
    ,mem_nickname = #{mem_nickname}
WHERE mem_id = #{mem_id}
