mapper.update:
UPDATE tb_notice
SET nt_title = #{nt_title}
  ,  nt_content = #{nt_content}
WHERE nt_no = #{nt_no}

mapper.select:
SELECT nt_no, nt_content, nt_cdate, nt_file, nt_hit, nt_cocnt
FROM tb_notice
WHERE nt_no = #{nt_no}

mapper.update:
UPDATE tb_notice
SET   nt_hit = nt_hit + 1
WHERE nt_no = #{nt_no}

mapper.select:
SELECT rnum,nt_no, nt_title, nt_content, nt_cdate, nt_file, nt_hit, nt_cocnt
        FROM(
            SELECT ROWNUM as rnum, a.*  FROM (
                SELECT nt_no, nt_title, nt_content, nt_cdate, nt_file, nt_hit,nt_cocnt  FROM tb_notice
                ORDER BY nt_no
            ) a
        )
WHERE #{offset} < rnum  and rnum <= #{offsetTo}

SELECT rnum,nt_no, nt_title, nt_content, nt_cdate, nt_file, nt_hit, nt_cocnt
        FROM(
            SELECT ROWNUM as rnum, a.*  FROM (
                SELECT nt_no, nt_title, nt_content, nt_cdate, nt_file, nt_hit, nt_cocnt  FROM tb_notice
                WHERE

                ORDER BY nt_cdate DESC, nt_no DESC
            ) a
        )
WHERE #{offset} < rnum  and rnum <= #{offsetTo}

mapper.update:
UPDATE tb_notice
SET NT_COCNT = nt_cocnt + #{cnt}
WHERE nt_no = #{nt_no}
