mapper.select:

SELECT p_no, p_title, p_author, p_img, p_publisher,  p_pubdate , categoryCode as categoryCode, p_description, p_price, p_like
	FROM (
		  SELECT rownum AS rn, p_no, p_title, p_author, p_img, p_publisher, p_pubdate, p_category as categoryCode, p_description, p_price, p_like
		  FROM TB_PRODUCT
		  where (P_TITLE


			OR
			P_AUTHOR )


					AND P_CATEGORY = #{categoryCode}



			AND
		      rownum <= #{pageNum} * #{amount}
	  	)
WHERE  rn >(#{pageNum}-1) * #{amount}

ORDER BY P_PUBDATE DESC

SELECT count(*) FROM tb_product WHERE (P_TITLE OR P_AUTHOR )
AND P_CATEGORY = #{categoryCode}
AND P_NO > 0
